import React from "react";

class GoogleAuth extends React.Component {
  state = { isSignedIn: null };
  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "542596897763-blpiqsce43je2tgo461k7p9pvvo7vrm6.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({ isSignedIn: this.auth.isSignedIn.get() });
        });
    });
  }

  renderAuthButton() {
    if (this.state.isSignedIn == null)
      return <div>Idk if we are signed in</div>;
    else if (this.state.signedIn) return <div>I am signed in!</div>;
    else return <div>I am not signed in!</div>;
  }

  render() {
    return <div>{this.renderAuthButton}</div>;
  }
}

export default GoogleAuth;
